import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import useFetch from "./useFetch";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

const PerDetails = () => {
  const { id } = useParams();
  const history = useHistory();
  const {
    data: persons,
    isLoading,
    error,
  } = useFetch("http://localhost:7088/persons/" + id);

  const handleDel = () => {
    fetch('http://localhost:7088/persons/' + persons.id, {
        method: 'DELETE'
    }).then(()=>{
        history.push('/')
    })
  }

  return (
    <div className="blog-deTales">
      {isLoading && <div>Loading Page . . .</div>}
      {error && <div>{error}</div>}
      {persons && (
        <article>
          <h2>weeitten by {persons.name}</h2>
          <p>
            Happens in {persons.city} at {persons.date}
          </p>
          <br />
          <p>{persons.story}</p>
          <button onClick={handleDel}>Delete</button>
        </article>
      )}
    </div>
  );
};

export default PerDetails;
