import { useEffect, useState } from "react";
import Blogs from "./Blogs";

const Home = () => {
    const [persons , changeInfo] = useState(null);
    const [isLoading, changeLoading] = useState(true);

    const deleter = (id) => {
        const thisPerson = persons.filter(person => person.id != id);
        changeInfo(thisPerson);
    }
    useEffect(() => {
        setTimeout(() => {
            fetch('http://localhost:7088/persons/')
            .then(reurwer => {
                console.log(reurwer);
                return reurwer.json();
            })
            .then(err => {
                console.log(err);
                changeInfo(err);
                changeLoading(false);
            })
            .catch(err => [
                alert(err.message)
            ]
            ) 
        }, 1000);
    }, []);

    return ( 
        <div className="home">
            {isLoading && <div>Loading Page . . .</div>}
            {persons && <Blogs persons={persons} title="non Native Americans" deleter={deleter}/>}
        </div>
     );
}
 
export default Home;
