import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import useFetch from "./useFetch";

const PerDetails = () => {
  const { id } = useParams();
  const { data: blog, isLoading, error } = useFetch();

  return (
    <div>
      {isLoading && <div>Loading Page . . .</div>}
      {error && <div>{error}</div>}
      { blog && 

        <h2>weeitten by { <blog className="name"></blog> }</h2>

      }
    </div>
  );
};

export default PerDetails;
