import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Update() {
    const { id } = useParams();
    const {value, changeValue} = useState({
      id: id,
      title: '',
story: '',
name: '',
date: '',
city: '',
    })

    useEffect(() => {
        axios
            .get("http://localhost:7088/tale/" + id)
            .then(res => console.log(res))
            .catch(err => console.log(err));
    }, []);

    return (
        <div className="create">
            <h2>Edit Tale</h2>
            <form>
                <label>Tale title</label>
                <input type="text" required />
                <label>Your story</label>
                <textarea required rows={12}></textarea>
                <label>Author name</label>
                <input type="text" />
                <label>Happen date</label>
                <input type="date" />
                <label>Happen city</label>
                <input type="text" />
                {/* {!adding && <button>add this new Tale</button>} */}
                {/* {adding && <button disabled>add Tale . . .</button>} */}
            </form>
        </div>
    );
}

export default Update;
