import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Update() {
    const { id } = useParams();
    const { Updates, changeUpdates } = useState({
        title: "",
        story: "",
        name: "",
        date: "",
        city: "",
        id: id
    });

    useEffect(() => {
        axios
            .get("http://localhost:7088/tale/" + id)
            .then(res => (changeUpdates({...Updates, 
              title: res.data.title,
              story: res.data.story,
              name: res.data.name,
              date: res.data.date,
              city: res.data.city
            })))
            .catch(err => console.log(err));
    }, [id]);

    return (
        <div className="create">
            <h2>Edit Tale</h2>
            <form>
                <label>Tale title</label>
                <input type="text" required value={Updates.title}/>
                <label>Your story</label>
                <textarea required rows={12} value={Updates.story}></textarea>
                <label>Author name</label>
                <input type="text" value={Updates.name}/>
                <label>Happen date</label>
                <input type="date" value={Updates.date}/>
                <label>Happen city</label>
                <input type="text" value={Updates.city}/>
                {/* {!adding && <button>add this new Tale</button>} */}
                {/* {adding && <button disabled>add Tale . . .</button>} */}
            </form>
        </div>
    );
}

export default Update;
