import { useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

const Create = () => {
    const [title, changeTile] = useState('');
    const [story, changeStory] = useState('');
    const [name, changeAurhor] = useState('');
    const [gender, changeGender] = useState('');
    const [city, changeCity] = useState('');

    const [adding, changeAdding] = useState(false)
    const history = useHistory();


    const submitKey = (e) => {
        e.preventDefault();
        const fullTail = {title, story,name,gender,city};
        
        changeAdding(true)
        fetch("http://localhost:7088/persons/", {
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(fullTail)
        }).then(() => {
            changeAdding(false)
            console.log('Your story add');
        })
    }

    return(
        <div className="create">
            <h2>Add new JEDI TAIL</h2>
            <form onSubmit={submitKey}>
                <label>Tail title</label>
                <input type="text" required value={title} onChange={(e) => changeTile(e.target.value)}/>
                <label>JEDI story</label>
                <textarea required rows={12} value={story} onChange={(e) => changeStory(e.target.value)}></textarea>
                <label>Author name</label>
                <input type="text" value={name} onChange={(e) => changeAurhor(e.target.value)}/>
                <label>Aithor gender (she or he)</label>
                <select value={gender} onChange={(e) => changeGender(e.target.value)}>
                    <option value="she">she</option>
                    <option value="he">he</option>
                </select>
                <label>Aithor city</label>
                <input type="text" value={city} onChange={(e) => changeCity(e.target.value)}/>
                {!adding && <button>add this new tail</button>}
                {adding && <button disabled>add tail . . .</button>}
            </form>
        </div>
        )
}
 
export default Create;
