import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import useFetch from "./useFetch";

const PerDetails = () => {
  const { id } = useParams();
  const { data: blog, isLoading, error } = useFetch("http://localhost:7088/person" + id);

  return (
    <div>
      {isLoading && <div>Loading Page . . .</div>}
      {error && <div>{error}</div>}
      {blog && (
        <article>
          <h2>weeitten by {blog.name}</h2>
          <p>
            {blog.gender} is living it {blog.city}
          </p>
          <br />
          <p>{blog.blog}</p>
        </article>
      )}
    </div>
  );
};

export default PerDetails;
