import Blogs from "./Blogs";
import useFetch from "./useFetch";


const Home = () => {
  const {data: persons, isLoading, error} = useFetch("http://localhost:7088/persons")

  const handleDelete = (id) => {
    const newBlogs = persons.filter(person => person.id !== id);
    persons(newBlogs);
  }

  return (
    <div className="home">
        {error && <div>{ error }</div>}
      {isLoading && <div>Loading Page . . .</div>}
      {persons && (
        <Blogs
          persons={persons}
          title="non Native Americans"
          handleDelete={handleDelete}
        />
      )}
    </div>
  );
};

export default Home;
