import { useState } from "react";

const Create = () => {
  const [title, changeTile] = useState("");
  const [story, changeStory] = useState("");
  const [author, changeAurhor] = useState("");
  const [gender, changeGender] = useState("");
  const [city, changeCity] = useState("");

  const [adding, changeAdding] = useState(false);

  const submitKey = (e) => {
    e.preventDefault();
    const fullTail = { title, story, author, gender, city };

    changeAdding(true);
    fetch("http://localhost:7088/persons/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(fullTail),
    }).then(() => {
      console.log("new blog added");
      changeAdding(false);
    });
  };

  return (
    <div className="create">
      <h2>Add new JEDI TAIL</h2>
      <form onSubmit={submitKey}>
        <label>Tail title</label>
        <input
          type="text"
          required
          value={title}
          onChange={(e) => changeTile(e.target.value)}
        />
        <label>JEDI story</label>
        <textarea
          required
          rows={12}
          value={story}
          onChange={(e) => changeStory(e.target.value)}
        ></textarea>
        <label>Author name</label>
        <input
          type="text"
          value={author}
          onChange={(e) => changeAurhor(e.target.value)}
        />
        <label>Author gender (she or he)</label>
        <select value={gender} onChange={(e) => changeGender(e.target.value)}>
          <option value="she">she</option>
          <option value="he">he</option>
        </select>
        <label>Author city</label>
        <input
          type="text"
          value={city}
          onChange={(e) => changeCity(e.target.value)}
        />
        {!adding && <button>add this new tail</button>}
        {adding && <button disabled>add tail . . .</button>}
      </form>
    </div>
  );
};

export default Create;
