import { useEffect, useState } from "react";
import Blogs from "./Blogs";

const Home = () => {
    const [persons , changeInfo] = useState(null);

    const [whoisit, changeName] = useState('pier')

    const deleter = (id) => {
        const thisPerson = persons.filter(person => person.id != id);
        changeInfo(thisPerson);
    }
    useEffect(() => {
        fetch('http://localhost:7088/persons/')
        .then(res => {
            return res.json
        })
    });

    return ( 
        <div className="home">
            <h1>{whoisit}</h1>
            <button type="button" onClick={() => changeName('mark')}>Change Whoisit</button>
            <Blogs persons={persons} title="non Native Americans" deleter={deleter}/>           
        </div>
     );
}
 
export default Home;
