import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import useFetch from "./useFetch";

const PerDetails = () => {
  const { id } = useParams();
  const {
    data: persons,
    isLoading,
    error,
  } = useFetch("http://localhost:7088/persons/" + id);

  return (
    <div className="blog-details">
      {isLoading && <div>Loading Page . . .</div>}
      {error && <div>{error}</div>}
      {persons && (
        <article>
          <h2>weeitten by {persons.name}</h2>
          <p>
            {persons.gender} is living it {persons.city}
          </p>
          <br />
          <p>{persons.story}</p>
        </article>
      )}
    </div>
  );
};

export default PerDetails;
