import { useEffect, useState } from "react";
import Blogs from "./Blogs";

const Home = () => {
  const [persons, changeInfo] = useState(null);
  const [isLoading, changeLoading] = useState(true);
  const [error, setError] = useState(null);

  const deleter = (id) => {
    const updatedPersons = persons.filter((person) => person.id !== id);
    changeInfo(updatedPersons);
  };

  useEffect(() => {
    setTimeout(() => {
      fetch("http://localhost:7088/persons")
        .then((res) => {
          if (!res.ok) {
            throw Error("We cant get infos sire");
          }
          return res.json();
        })
        .then((data) => {
          changeInfo(data);
          changeLoading(false);
        })
        .catch((err) => {
          setError(err.message);
        });
    }, 1000);
  }, []);

  return (
    <div className="home">
      {isLoading && <div>Loading Page . . .</div>}
      {error && <div>Error: {error}</div>}
      {persons && (
        <Blogs
          persons={persons}
          title="non Native Americans"
          deleter={deleter}
        />
      )}
    </div>
  );
};

export default Home;
